const mongoose = require('mongoose');
const logger = require('./logger');

const connectDB = async () => {
  try {
    await mongoose.connect(process.env.MONGODB_URI);
    logger.info('MongoDB connected successfully.');
  } catch (error) {
    logger.error('MongoDB connection failed:', { message: error.message, stack: error.stack });
    // Exit process with failure
    process.exit(1);
  }
};

module.exports = connectDB;

const mongoose = require('mongoose');

const userSchema = new mongoose.Schema({
  userId: {
    type: String,
    required: true,
    unique: true,
  },
  username: {
    type: String,
    required: true,
  },
  userColor: {
    type: String,
    required: true,
  }
}, { timestamps: true });

const User = mongoose.model('User', userSchema);

module.exports = User;

const mongoose = require('mongoose');

const reactionSchema = new mongoose.Schema({
  userId: { type: String, required: true },
  username: { type: String, required: true },
}, { _id: false });

const messageSchema = new mongoose.Schema({
  id: { type: String, required: true, unique: true },
  userId: { type: String, required: true },
  username: { type: String, required: true },
  userColor: { type: String, required: true },
  sender: { type: String, required: true }, // This seems to be the same as userId, can be refactored later
  text: { type: String },
  timestamp: { type: Date, default: Date.now },
  type: { type: String, default: 'text' }, // 'text', 'image', 'video'
  url: { type: String },
  originalName: { type: String },
  isDeleted: { type: Boolean, default: false },
  edited: { type: Boolean, default: false },
  replyingTo: {
    id: String,
    username: String,
    text: String,
    type: String,
  },
  reactions: {
    type: Map,
    of: [reactionSchema],
  }
}, { timestamps: true });

const Message = mongoose.model('Message', messageSchema);

module.exports = Message;

const mongoose = require('mongoose');

const messageEventSchema = new mongoose.Schema({
  type: {
    type: String,
    required: true,
    enum: ['create', 'edit', 'delete_everyone', 'upload'],
  },
  messageId: { type: String },
  deletedContent: { type: mongoose.Schema.Types.Mixed },
  oldText: { type: String },
  newText: { type: String },
  file: {
    originalname: String,
    mimetype: String,
    size: Number,
  },
  message: { type: mongoose.Schema.Types.Mixed },
  userId: { type: String, required: true },
  username: { type: String, required: true },
  timestamp: { type: Date, required: true },
}, { timestamps: true });

const MessageEvent = mongoose.model('MessageEvent', messageEventSchema);

module.exports = MessageEvent;

