        wss.clients.forEach(c => {
            if (c.readyState === WebSocket.OPEN) {
                c.send(JSON.stringify(updateMsg));
            }
        });
        break;
      }
      default: { // New text/media message
        if (ws.userId && typingUsers.has(ws.userId)) {
