const getUserId = (): string => {
  let userId = localStorage.getItem('pulseUserId');
  if (!userId) {
    userId = Date.now().toString() + Math.random().toString(36).substring(2);
    localStorage.setItem('pulseUserId', userId);
  }
  return userId;
};

