  width: 300px; /* Inherit width from Select */
  margin-bottom: 1rem; /* Inherit margin from Select */
  flex: 1; /* Inherit flex from Input/Select */
  min-width: 180px; /* Inherit min-width from Input/Select */

  /* Removed &::after to revert to native arrow appearance */
`;

const Select = styled.select`
  padding: 0.75rem 1rem;
  padding-right: 2rem; /* Keep this as user said position is correct now */
  font-size: 1rem;
  /* Reset width, margin-bottom, flex, min-width as they are now handled by SelectWrapper */
  width: 100%;
  margin-bottom: 0; 
  flex: none; 
  min-width: auto;

  border: 1px solid #cbd5e0;
  border-radius: 4px;
  background-color: white;
  transition: all 0.2s;
  /* Removed -webkit-appearance, -moz-appearance, appearance, and ::-ms-expand to revert to native arrow appearance */

  &:focus {
    outline: none;
    border-color: #3B82F6;
    box-shadow: 0 0 0 2px #bfdbfe;
  }
`;

const FilterContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 1rem;
  margin-bottom: 1.5rem;
  padding: 1rem;
  background-color: #f0f4f8;
  border-radius: 8px;
  box-shadow: inset 0 1px 3px rgba(0,0,0,0.05);

  ${Input}, ${SelectWrapper} { /* Changed to SelectWrapper */
    flex: 1;
    min-width: 180px;
  }
`;

const Button = styled.button`
  padding: 0.75rem 1.5rem;
  font-size: 1rem;
  cursor: pointer;
  background-color: #3B82F6;
  color: white;
  border: none;
  border-radius: 4px;
  transition: background-color 0.2s;
  &:hover {
    background-color: #2563EB;
  }
  &:disabled {
    background-color: #9ca3af;
    cursor: not-allowed;
  }
`;

const ErrorMessage = styled.p`
  color: #EF4444;
  margin-top: 1rem;
`;

const TabContainer = styled.div`
  display: flex;
  border-bottom: 1px solid #cbd5e0;
  margin-bottom: -1px; /* To make tab content border connect */
`;

const TabButton = styled.button<{ active: boolean }>`
  padding: 0.75rem 1.5rem;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  background-color: ${props => props.active ? 'white' : 'transparent'};
  color: ${props => props.active ? '#3B82F6' : '#4a5568'};
  border: 1px solid ${props => props.active ? '#cbd5e0' : 'transparent'};
  border-bottom: 1px solid ${props => props.active ? 'white' : '#cbd5e0'};
  margin-bottom: -1px;
  border-top-left-radius: 0.25rem;
  border-top-right-radius: 0.25rem;
  outline: none;
  transition: all 0.2s;
