`;

const TabContent = styled.div`
  border: 1px solid #cbd5e0;
  padding: 2rem;
  border-radius: 0 0.25rem 0.25rem 0.25rem;
  background-color: white;
  flex-grow: 1; /* Added */
  display: flex; /* Added */
  flex-direction: column; /* Added */
`;


const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
  margin-top: 1rem;
  font-size: 0.9rem;
`;

const Th = styled.th`
  padding: 0.75rem;
  text-align: left;
  border-bottom: 1px solid #e2e8f0;
  background-color: #f7fafc;
  font-weight: 600;
  color: #4a5568;
`;

const Td = styled.td`
  padding: 0.75rem;
  text-align: left;
  border-bottom: 1px solid #e2e8f0;
  word-break: break-word;
`;

const LogoutButton = styled(Button)`
  position: absolute;
  top: 2rem;
  right: 2rem;
  background-color: #EF4444;
  &:hover {
    background-color: #DC2626;
  }
`;

const ActivityLogContainer = styled.div`
  /* Removed fixed height, now will grow */
  flex-grow: 1; /* Added */
  width: 100%; /* Added */
  background-color: #1a202c;
  color: #e2e8f0;
  padding: 1rem;
  border-radius: 4px;
  font-family: 'Courier New', Courier, monospace;
  display: flex;
  flex-direction: column-reverse; /* New items appear at the top */
  overflow: hidden; /* Hide scrollbar */
`;

const LogViewerContainer = styled.pre`
  flex-grow: 1; /* Added */
  width: 100%; /* Added */
  overflow-y: auto; /* Scrollable for long logs */
  background-color: #1a202c;
  color: #e2e8f0;
  padding: 1rem;
  border-radius: 4px;
  font-family: 'Courier New', Courier, monospace;
  white-space: pre-wrap; /* Preserve formatting but wrap lines */
  word-wrap: break-word; /* Break long words */
`;

const ActivityLogItem = styled.div`
  padding: 0.25rem 0;
`;

interface UserProfile {
  userId: string;
  username: string;
}

type Tab = 'messages' | 'users' | 'activity' | 'logs';

const formatDate = (dateString: string) => {
  const date = new Date(dateString);
  return date.toLocaleDateString('en-GB', { day: '2-digit', month: 'short', year: 'numeric' });
};

const formatTime = (dateString: string) => {
  const date = new Date(dateString);
  return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit', second: '2-digit' });
};

const Admin = () => {
  const [password, setPassword] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [error, setError] = useState('');
  const [users, setUsers] = useState<UserProfile[]>([]);
  const [historyLogs, setHistoryLogs] = useState<any[]>([]);
  const [serverLogs, setServerLogs] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [activeTab, setActiveTab] = useState<Tab>('messages');
  const [activityLogs, setActivityLogs] = useState<string[]>(() => {
    const savedLogs = sessionStorage.getItem('admin-activity-logs');
    return savedLogs ? JSON.parse(savedLogs) : [];
  });
  const ws = useRef<WebSocket | null>(null);

  // Filter states
  const [filterMessageId, setFilterMessageId] = useState('');
  const [filterUser, setFilterUser] = useState('');
  const [filterEventType, setFilterEventType] = useState('All');
  const [filterContent, setFilterContent] = useState('');

  useEffect(() => {
    const checkAuthAndFetchData = async () => {
      const storedPassword = sessionStorage.getItem('admin-password');
      if (storedPassword) {
        setIsLoading(true);
        try {
          const [usersResponse, historyResponse, serverLogsResponse] = await Promise.all([
            fetch('http://localhost:8080/api/admin/users', { headers: { 'x-admin-password': storedPassword } }),
            fetch('http://localhost:8080/api/admin/history', { headers: { 'x-admin-password': storedPassword } }),
            fetch('http://localhost:8080/api/admin/server-logs', { headers: { 'x-admin-password': storedPassword } })
          ]);

          if (usersResponse.ok && historyResponse.ok && serverLogsResponse.ok) {
            const usersData = await usersResponse.json();
            const historyData = await historyResponse.json();
            const serverLogsData = await serverLogsResponse.text();
            setUsers(usersData);
            setHistoryLogs(historyData.sort((a: any, b: any) => new Date(b.timestamp).getTime() - new Date(a.timestamp).getTime()));
            setServerLogs(serverLogsData);
            setIsAuthenticated(true);
          } else {
            sessionStorage.removeItem('admin-password');
          }
        } catch (err) {
          console.error("Failed to fetch admin data", err);
          sessionStorage.removeItem('admin-password');
        }
        setIsLoading(false);
      }
    };
    checkAuthAndFetchData();
  }, []);

  useEffect(() => {
    if (!isAuthenticated) return;

    const storedPassword = sessionStorage.getItem('admin-password');
    if (!storedPassword) return;

    const wsUrl = `ws://localhost:8080?adminPassword=${storedPassword}`;
    ws.current = new WebSocket(wsUrl);
    ws.current.onopen = () => console.log('Admin WebSocket connected');
    ws.current.onmessage = (event) => {
      const message = JSON.parse(event.data);
      if (message.type === 'activity') {
        setActivityLogs(prevLogs => {
          const newLogs = [`[${new Date().toLocaleTimeString()}] ${message.activity}`, ...prevLogs].slice(0, 50);
          sessionStorage.setItem('admin-activity-logs', JSON.stringify(newLogs));
          return newLogs;
        });
      }
    };
    ws.current.onclose = () => console.log('Admin WebSocket disconnected');
    ws.current.onerror = (error) => console.error('Admin WebSocket error:', error);

    return () => {
      if (ws.current) ws.current.close();
    };
  }, [isAuthenticated]);

  const handleLogin = async () => {
    setIsLoading(true);
    setError('');
    try {
      const [usersResponse, historyResponse, serverLogsResponse] = await Promise.all([
        fetch('http://localhost:8080/api/admin/users', { headers: { 'x-admin-password': password } }),
        fetch('http://localhost:8080/api/admin/history', { headers: { 'x-admin-password': password } }),
        fetch('http://localhost:8080/api/admin/server-logs', { headers: { 'x-admin-password': password } })
      ]);

      if (usersResponse.ok && historyResponse.ok && serverLogsResponse.ok) {
        sessionStorage.setItem('admin-password', password);
        const usersData = await usersResponse.json();
        const historyData = await historyResponse.json();
        const serverLogsData = await serverLogsResponse.text();
        setUsers(usersData);
        setHistoryLogs(historyData.sort((a: any, b: any) => new Date(b.timestamp).getTime() - new Date(a.timestamp).getTime()));
        setServerLogs(serverLogsData);
        setIsAuthenticated(true);
      } else {
        setError('Incorrect password.');
      }
    } catch (err) {
      setError('An error occurred while trying to log in.');
    }
    setIsLoading(false);
  };

  const handleLogout = () => {
    if (ws.current) ws.current.close();
    sessionStorage.removeItem('admin-password');
    sessionStorage.removeItem('admin-activity-logs');
    setIsAuthenticated(false);
    setPassword('');
    setActivityLogs([]);
  }

  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') handleLogin();
  };

  const renderEventType = (logType: string) => {
    switch (logType) {
        case 'create': return 'Create';
        case 'edit': return 'Edit';
        case 'upload': return 'Upload';
        case 'delete_everyone': return 'Delete (Everyone)';
        default: return logType;
    }
  }

  const renderMessageDetails = (log: any) => {
    const formatMedia = (content: any) => {
      if (!content) return '"[Empty]"';
      const text = content.text || '';
      if (content.url) {
        const isGif = content.url.includes('tenor.com');
        const fileName = content.originalName || (isGif ? 'GIF' : 'Uploaded File');
        return <>{text && `"${text}" `}<a href={content.url} target="_blank" rel="noopener noreferrer">[{fileName}]</a></>;
      }
      return `"${text}"`;
    };

    switch (log.type) {
      case 'create':
        return <>Content: {formatMedia(log.message)}</>;
      case 'edit':
        return <>Old: "{log.oldText}" → New: "{log.newText}"</>;
      case 'delete_everyone':
        return <>Deleted Content: {formatMedia(log.deletedContent)}</>;
      case 'upload':
        return `File: '${log.file.originalname}' (${(log.file.size / 1024).toFixed(2)} KB)`;
      default:
        return JSON.stringify(log);
    }
  };

  const filteredHistoryLogs = useMemo(() => {
    return historyLogs.filter(log => {
      const messageIdMatch = filterMessageId ? (log.messageId || log.message?.id)?.toLowerCase().includes(filterMessageId.toLowerCase()) : true;
      const userMatch = filterUser ? (log.username || '').toLowerCase().includes(filterUser.toLowerCase()) : true;
      const eventTypeMatch = filterEventType === 'All' ? true : log.type === filterEventType.toLowerCase().replace(' (everyone)', '_everyone'); // Convert "Delete (Everyone)" back

      let contentMatch = true;
      if (filterContent) {
        const lowerCaseFilter = filterContent.toLowerCase();
        if (log.type === 'create' && log.message?.text) {
          contentMatch = log.message.text.toLowerCase().includes(lowerCaseFilter);
        } else if (log.type === 'edit') {
          contentMatch = (log.oldText && log.oldText.toLowerCase().includes(lowerCaseFilter)) || (log.newText && log.newText.toLowerCase().includes(lowerCaseFilter));
        } else if (log.type === 'delete_everyone' && log.deletedContent?.text) {
          contentMatch = log.deletedContent.text.toLowerCase().includes(lowerCaseFilter);
        } else if (log.type === 'upload' && log.file?.originalname) {
          contentMatch = log.file.originalname.toLowerCase().includes(lowerCaseFilter);
        }
      }

      return messageIdMatch && userMatch && eventTypeMatch && contentMatch;
    });
  }, [historyLogs, filterMessageId, filterUser, filterEventType, filterContent]);

  if (!isAuthenticated) {
    return (
      <LoginFormContainer>
        <LoginBox>
          <Title>Admin Login</Title>
          <Input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            onKeyDown={handleKeyDown}
          />
          <Button onClick={handleLogin} disabled={isLoading}>{isLoading ? 'Logging in...' : 'Login'}</Button>
          {error && <ErrorMessage>{error}</ErrorMessage>}
        </LoginBox>
      </LoginFormContainer>
    );
  }

  return (
    <AdminContainer>
      <Title>Admin Panel</Title>
      <LogoutButton onClick={handleLogout}>Logout</LogoutButton>

      <TabContainer>
        <TabButton active={activeTab === 'messages'} onClick={() => setActiveTab('messages')}>Message Log</TabButton>
        <TabButton active={activeTab === 'users'} onClick={() => setActiveTab('users')}>Users</TabButton>
        <TabButton active={activeTab === 'activity'} onClick={() => setActiveTab('activity')}>Live Activity</TabButton>
        <TabButton active={activeTab === 'logs'} onClick={() => setActiveTab('logs')}>Server Logs</TabButton>
      </TabContainer>

      <TabContent>
        {activeTab === 'messages' && (
          <>
            <h2>Message Log</h2>
            <FilterContainer>
              <Input
                type="text"
                placeholder="Filter by Message ID"
                value={filterMessageId}
                onChange={(e) => setFilterMessageId(e.target.value)}
              />
              <Input
                type="text"
                placeholder="Filter by User"
                value={filterUser}
                onChange={(e) => setFilterUser(e.target.value)}
              />
              <SelectWrapper>
                <Select
                  value={filterEventType}
                  onChange={(e) => setFilterEventType(e.target.value)}
                >
                  <option value="All">All Events</option>
                  <option value="Create">Create</option>
                  <option value="Edit">Edit</option>
                  <option value="Upload">Upload</option>
                  <option value="Delete (Everyone)">Delete (Everyone)</option>
                </Select>
              </SelectWrapper>
              <Input
                type="text"
                placeholder="Filter by Content"
                value={filterContent}
                onChange={(e) => setFilterContent(e.target.value)}
              />
            </FilterContainer>
            {isLoading ? (
                <p>Loading history...</p>
            ) : (
                <Table>
                    <thead>
                        <tr>
                            <Th>Date</Th>
                            <Th>Time</Th>
                            <Th>Event</Th>
                            <Th>User</Th>
                            <Th>Message ID</Th>
                            <Th>Details</Th>
                        </tr>
                    </thead>
                    <tbody>
                        {filteredHistoryLogs.map((log, index) => (
                            <tr key={index}>
                                <Td>{formatDate(log.timestamp)}</Td>
                                <Td>{formatTime(log.timestamp)}</Td>
                                <Td>{renderEventType(log.type)}</Td>
                                <Td>{log.username} ({log.userId})</Td>
                                <Td>{log.messageId || log.message?.id || 'N/A'}</Td>
                                <Td>{renderMessageDetails(log)}</Td>
                            </tr>
                        ))}
                    </tbody>
                </Table>
            )}
          </>
        )}

        {activeTab === 'users' && (
          <>
            <h2>Users</h2>
            {isLoading ? (
              <p>Loading users...</p>
            ) : (
              <Table>
                <thead>
                  <tr>
                    <Th>User ID</Th>
                    <Th>Username</Th>
                  </tr>
                </thead>
                <tbody>
                  {users.map(user => (
                    <tr key={user.userId}>
                      <Td>{user.userId}</Td>
                      <Td>{user.username}</Td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            )}
          </>
        )}

        {activeTab === 'activity' && (
            <>
                <h2>Real-Time Activity</h2>
                <ActivityLogContainer>
                {activityLogs.map((log, index) => (
                    <ActivityLogItem key={index}>{log}</ActivityLogItem>
                ))}
                </ActivityLogContainer>
            </>
        )}

        {activeTab === 'logs' && (
            <>
                <h2>Server Logs</h2>
                {isLoading ? (
                    <p>Loading server logs...</p>
                ) : (
                    <LogViewerContainer>
                        {serverLogs}
                    </LogViewerContainer>
                )}
            </>
        )}
      </TabContent>
    </AdminContainer>
  );
};

export default Admin;

