                    <thead>
                        <tr>
                            <Th>Date</Th>
                            <Th>Time</Th>
                            <Th>Event</Th>
                            <Th>User</Th>
                            <Th>Message ID</Th>
                            <Th>Details</Th>
                        </tr>
                    </thead>
                    <tbody>
                        {filteredHistoryLogs.map((log, index) => (
                            <tr key={index}>
                                <Td>{formatDate(log.timestamp)}</Td>
                                <Td>{formatTime(log.timestamp)}</Td>
                                <Td>{renderEventType(log.type)}</Td>
                                <Td>{log.username} ({log.userId})</Td>
                                <Td>{log.messageId || log.message?.id || 'N/A'}</Td>
                                <Td>{renderMessageDetails(log)}</Td>
                            </tr>
                        ))}
                    </tbody>
                </Table>
            )}
          </>
        )}

        {activeTab === 'users' && (
          <>
            <h2>Users</h2>
            {isLoading ? (
              <p>Loading users...</p>
            ) : (
              <Table>
                <thead>
                  <tr>
                    <Th>User ID</Th>
                    <Th>Username</Th>
                  </tr>
                </thead>
                <tbody>
                  {users.map(user => (
                    <tr key={user.userId}>
                      <Td>{user.userId}</Td>
                      <Td>{user.username}</Td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            )}
          </>
        )}

        {activeTab === 'activity' && (
            <>
                <h2>Real-Time Activity</h2>
                <LogWrapper>
                    <ActivityLogContainer>
                    {activityLogs.map((log, index) => (
                        <ActivityLogItem key={index}>{log}</ActivityLogItem>
                    ))}
                    </ActivityLogContainer>
                </LogWrapper>
            </>
        )}

        {activeTab === 'logs' && (
            <>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <h2>Server Logs</h2>
                <Button onClick={handleRefreshServerLogs}>Refresh</Button>
              </div>
              {isLoading ? (
                  <p>Loading server logs...</p>
              ) : (
                  <LogWrapper>
                      <LogViewerContainer>
                          {serverLogs}
                      </LogViewerContainer>
                  </LogWrapper>
              )}
            </>
        )}
      </TabContent>
