  };

  const handleLogout = () => {
    if (ws.current) ws.current.close();
    sessionStorage.removeItem('admin-password');
    sessionStorage.removeItem('admin-activity-logs');
    setIsAuthenticated(false);
    setPassword('');
    setActivityLogs([]);
  }

  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') handleLogin();
  };

  const handleRefreshServerLogs = async () => {
    const storedPassword = sessionStorage.getItem('admin-password');
    if (storedPassword) {
      try {
        const serverLogsResponse = await fetch('http://localhost:8080/api/admin/server-logs', { headers: { 'x-admin-password': storedPassword } });
        if (serverLogsResponse.ok) {
          const serverLogsData = await serverLogsResponse.text();
          setServerLogs(serverLogsData);
        }
      } catch (err) {
        console.error("Failed to fetch server logs", err);
      }
    }
  };

  const renderEventType = (logType: string) => {
    switch (logType) {
        case 'create': return 'Create';
        case 'edit': return 'Edit';
        case 'upload': return 'Upload';
        case 'delete_everyone': return 'Delete (Everyone)';
        default: return logType;
    }
  }

  const renderMessageDetails = (log: any) => {
    const formatMedia = (content: any) => {
      if (!content) return '"[Empty]"';
      const text = content.text || '';
      if (content.url) {
        const isGif = content.url.includes('tenor.com');
        const fileName = content.originalName || (isGif ? 'GIF' : 'Uploaded File');
        return <>{text && `"${text}" `}<a href={content.url} target="_blank" rel="noopener noreferrer">[{fileName}]</a></>;
      }
      return `"${text}"`;
    };

    switch (log.type) {
      case 'create':
        return <>Content: {formatMedia(log.message)}</>;
      case 'edit':
        return <>Old: "{log.oldText}" → New: "{log.newText}"</>;
      case 'delete_everyone':
        return <>Deleted Content: {formatMedia(log.deletedContent)}</>;
      case 'upload':
        return `File: '${log.file.originalname}' (${(log.file.size / 1024).toFixed(2)} KB)`;
      default:
        return JSON.stringify(log);
    }
  };

  const enrichedHistoryLogs = useMemo(() => {
    const userMap = new Map(users.map(u => [u.userId, u.username]));
    return historyLogs.map(log => {
      const username = log.username || userMap.get(log.userId) || 'Unknown';
      return {
        ...log,
        username: username,
      };
    });
  }, [historyLogs, users]);

  const filteredHistoryLogs = useMemo(() => {
    return enrichedHistoryLogs.filter(log => {
      const messageIdMatch = filterMessageId ? (log.messageId || log.message?.id)?.toLowerCase().includes(filterMessageId.toLowerCase()) : true;
      const userMatch = filterUser ? (log.username || '').toLowerCase().includes(filterUser.toLowerCase()) : true;
      const eventTypeMatch = filterEventType === 'All' ? true : log.type === filterEventType.toLowerCase().replace(' (everyone)', '_everyone');

      const contentMatch = !filterContent ? true : (() => {
        const lowerCaseFilter = filterContent.toLowerCase();
        if (log.type === 'create' && log.message?.text) {
            return log.message.text.toLowerCase().includes(lowerCaseFilter);
        }
        if (log.type === 'edit') {
            return (log.oldText && log.oldText.toLowerCase().includes(lowerCaseFilter)) || (log.newText && log.newText.toLowerCase().includes(lowerCaseFilter));
        }
        if (log.type === 'delete_everyone' && log.deletedContent?.text) {
            return log.deletedContent.text.toLowerCase().includes(lowerCaseFilter);
        }
        if (log.type === 'upload' && log.file?.originalname) {
            return log.file.originalname.toLowerCase().includes(lowerCaseFilter);
        }
        return false;
      })();

      return messageIdMatch && userMatch && eventTypeMatch && contentMatch;
    });
  }, [enrichedHistoryLogs, filterMessageId, filterUser, filterEventType, filterContent]);

  if (!isAuthenticated) {
    return (
      <LoginFormContainer>
        <LoginBox>
          <Title>Admin Login</Title>
          <Input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            onKeyDown={handleKeyDown}
          />
          <Button onClick={handleLogin} disabled={isLoading}>{isLoading ? 'Logging in...' : 'Login'}</Button>
          {error && <ErrorMessage>{error}</ErrorMessage>}
        </LoginBox>
      </LoginFormContainer>
    );
  }

  return (
    <AdminContainer>
      <Title>Admin Panel</Title>
      <LogoutButton onClick={handleLogout}>Logout</LogoutButton>

      <TabContainer>
        <TabButton active={activeTab === 'messages'} onClick={() => setActiveTab('messages')}>Message Log</TabButton>
        <TabButton active={activeTab === 'users'} onClick={() => setActiveTab('users')}>Users</TabButton>
        <TabButton active={activeTab === 'activity'} onClick={() => setActiveTab('activity')}>Live Activity</TabButton>
        <TabButton active={activeTab === 'logs'} onClick={() => setActiveTab('logs')}>Server Logs</TabButton>
      </TabContainer>

      <TabContent>
        {activeTab === 'messages' && (
          <>
            <h2>Message Log</h2>
            <FilterContainer>
              <Input
                type="text"
                placeholder="Filter by Message ID"
                value={filterMessageId}
                onChange={(e) => setFilterMessageId(e.target.value)}
              />
              <Input
                type="text"
                placeholder="Filter by User"
                value={filterUser}
                onChange={(e) => setFilterUser(e.target.value)}
              />
              <SelectWrapper>
                <Select
                  value={filterEventType}
                  onChange={(e) => setFilterEventType(e.target.value)}
                >
                  <option value="All">All Events</option>
                  <option value="Create">Create</option>
                  <option value="Edit">Edit</option>
                  <option value="Upload">Upload</option>
                  <option value="Delete (Everyone)">Delete (Everyone)</option>
                </Select>
              </SelectWrapper>
              <Input
                type="text"
                placeholder="Filter by Content"
                value={filterContent}
                onChange={(e) => setFilterContent(e.target.value)}
              />
            </FilterContainer>
            {isLoading ? (
                <p>Loading history...</p>
            ) : (
                <Table>
                    <thead>
                        <tr>
                            <Th>Date</Th>
                            <Th>Time</Th>
                            <Th>Event</Th>
                            <Th>User</Th>
                            <Th>Message ID</Th>
                            <Th>Details</Th>
                        </tr>
                    </thead>
                    <tbody>
                        {filteredHistoryLogs.map((log, index) => (
                            <tr key={index}>
                                <Td>{formatDate(log.timestamp)}</Td>
                                <Td>{formatTime(log.timestamp)}</Td>
                                <Td>{renderEventType(log.type)}</Td>
                                <Td>{log.username} ({log.userId})</Td>
                                <Td>{log.messageId || log.message?.id || 'N/A'}</Td>
                                <Td>{renderMessageDetails(log)}</Td>
                            </tr>
                        ))}
                    </tbody>
                </Table>
            )}
          </>
        )}

        {activeTab === 'users' && (
          <>
            <h2>Users</h2>
            {isLoading ? (
              <p>Loading users...</p>
            ) : (
              <Table>
                <thead>
                  <tr>
                    <Th>User ID</Th>
                    <Th>Username</Th>
                  </tr>
                </thead>
                <tbody>
                  {users.map(user => (
                    <tr key={user.userId}>
                      <Td>{user.userId}</Td>
                      <Td>{user.username}</Td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            )}
          </>
        )}

        {activeTab === 'activity' && (
            <>
                <h2>Real-Time Activity</h2>
                <ActivityLogContainer>
                {activityLogs.map((log, index) => (
                    <ActivityLogItem key={index}>{log}</ActivityLogItem>
                ))}
                </ActivityLogContainer>
            </>
        )}

        {activeTab === 'logs' && (
            <>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <h2>Server Logs</h2>
                <Button onClick={handleRefreshServerLogs}>Refresh</Button>
              </div>
              {isLoading ? (
                  <p>Loading server logs...</p>
              ) : (
                  <LogViewerContainer>
                      {serverLogs}
                  </LogViewerContainer>
              )}
            </>
        )}
      </TabContent>
    </AdminContainer>
  );
};

export default Admin;

require('dotenv').config();
const http = require('http');
const express = require('express');
const axios = require('axios');
const { WebSocketServer, WebSocket } = require('ws');
const cors = require('cors');
const cloudinary = require('cloudinary').v2;
const { CloudinaryStorage } = require('multer-storage-cloudinary');
const multer = require('multer');
const logger = require('./logger');
const fs = require('fs');
const path = require('path');

// --- In-Memory Stores ---
let messageHistory = [];
const MAX_HISTORY = 200;
const messageEventsLog = [];
const onlineUsers = new Map();
const allUsers = new Map(); // Keep a record of all users ever connected
const typingUsers = new Map();
const adminClients = new Set();

// --- Cloudinary & Multer Configuration ---
cloudinary.config({
  cloud_name: process.env.CLOUDINARY_CLOUD_NAME,
  api_key: process.env.CLOUDINARY_API_KEY,
  api_secret: process.env.CLOUDINARY_API_SECRET,
});
const storage = new CloudinaryStorage({
  cloudinary: cloudinary,
  params: {
    folder: 'pulse-chat',
    resource_type: 'auto',
  },
});
const upload = multer({ storage });

// --- Server Setup ---
const app = express();
const server = http.createServer(app);
const wss = new WebSocketServer({ server });
const PORT = process.env.PORT || 8080;

// --- Middleware ---
app.use(cors({ methods: ["GET", "POST", "DELETE"] }));
app.use(express.json());

// --- Helper Functions ---
const broadcastToAdmins = (type, data) => {
  const message = JSON.stringify({ type, data });
  adminClients.forEach(client => {
    if (client.readyState === WebSocket.OPEN) {
      client.send(message);
    }
  });
};

const broadcastOnlineUsers = () => {
  const users = Array.from(onlineUsers.values()).map(user => ({
    ...user,
    isTyping: typingUsers.has(user.userId)
  }));
  const message = JSON.stringify({ type: 'online_users', data: users });
  wss.clients.forEach(client => {
    if (client.readyState === WebSocket.OPEN) client.send(message);
  });
};

// Middleware for admin authentication
const adminAuth = (req, res, next) => {
    const password = req.headers['x-admin-password'];
    if (password && password === process.env.ADMIN_PASSWORD) {
        next();
    } else {
        res.status(401).json({ error: 'Unauthorized' });
    }
};

// --- Main Routes ---
app.get('/', (req, res) => res.send('Pulse Chat Server is running!'));

app.post('/api/upload', upload.single('file'), (req, res) => {
  if (!req.file) return res.status(400).json({ error: 'No file uploaded.' });

  const { userId } = req.body;
  const username = allUsers.get(userId)?.username || 'Unknown';

  messageEventsLog.push({
    type: 'upload',
    file: {
        originalname: req.file.originalname,
        mimetype: req.file.mimetype,
        size: req.file.size,
    },
    userId,
    username,
    timestamp: new Date().toISOString(),
  });
  broadcastToAdmins('history', messageEventsLog);
  broadcastToAdmins('activity', `File '${req.file.originalname}' uploaded by '${username}'.`);

  logger.info(`File uploaded: ${req.file.originalname}`);
  res.status(200).json({
    id: req.file.filename,
    type: req.file.resource_type,
    url: req.file.path,
    originalName: req.file.originalname,
    text: req.body.text,
  });
});

app.delete('/api/delete/:id', async (req, res) => {
  try {
    const messageId = req.params.id;
    if (!messageId) return res.status(400).json({ error: 'No message ID provided.' });
    logger.info(`Delete request for message ID: ${messageId}`);
    if (messageId.includes('/')) {
      await cloudinary.uploader.destroy(messageId, { resource_type: 'video', invalidate: true });
      await cloudinary.uploader.destroy(messageId, { resource_type: 'image', invalidate: true });
    }
    messageHistory = messageHistory.filter(msg => msg.id !== messageId);
    const deleteMessage = { type: 'delete', id: messageId };
    wss.clients.forEach(client => {
      if (client.readyState === WebSocket.OPEN) client.send(JSON.stringify(deleteMessage));
    });
    res.status(200).json({ success: true, message: 'Message deleted.' });
  } catch (error) {
    logger.error('Delete error:', { message: error.message, stack: error.stack });
    res.status(500).json({ error: 'Failed to delete file.' });
  }
});

// --- GIF API Routes ---
const TENOR_API_KEY = process.env.TENOR_API_KEY;
const TENOR_API_URL = 'https://tenor.googleapis.com/v2';

app.get('/api/gifs/trending', async (req, res) => {
  try {
    const response = await axios.get(`${TENOR_API_URL}/featured`, { params: { key: TENOR_API_KEY, limit: 20 } });
    const gifs = response.data.results.map((gif) => ({ id: gif.id, preview: gif.media_formats.tinygif.url, url: gif.media_formats.gif.url }));
    res.json(gifs);
  } catch (error) {
    logger.error('Error fetching trending GIFs:', { message: error.message });
    res.status(500).json({ error: 'Failed to fetch GIFs' });
  }
});

app.get('/api/gifs/search', async (req, res) => {
  try {
    const query = req.query.q;
    if (!query) return res.status(400).json({ error: 'Search query is required' });
    const response = await axios.get(`${TENOR_API_URL}/search`, { params: { key: TENOR_API_KEY, q: query, limit: 20 } });
    const gifs = response.data.results.map((gif) => ({ id: gif.id, preview: gif.media_formats.tinygif.url, url: gif.media_formats.gif.url }));
    res.json(gifs);
  } catch (error) {
    logger.error('Error searching GIFs:', { message: error.message });
    res.status(500).json({ error: 'Failed to fetch GIFs' });
  }
});

// --- Admin Routes ---
app.get('/api/admin/messages', adminAuth, (req, res) => {
    res.json(messageHistory);
});

app.get('/api/admin/users', adminAuth, (req, res) => {
    res.json(Array.from(allUsers.values()));
});

app.get('/api/admin/history', adminAuth, (req, res) => {
    res.json(messageEventsLog);
});

app.get('/api/admin/server-logs', adminAuth, (req, res) => {
  const logFilePath = path.join(__dirname, 'pulse-activity.log');
  fs.readFile(logFilePath, 'utf8', (err, data) => {
    if (err) {
      logger.error('Failed to read log file:', err);
      return res.status(500).send('Could not read server logs.');
    }
    const lines = data.split('\n').slice(-200).join('\n');
    res.type('text/plain').send(lines);
  });
});

// --- WebSocket Connection Logic ---
wss.on('connection', (ws, req) => {
  const url = new URL(req.url, `http://${req.headers.host}`);
  const adminPassword = url.searchParams.get('adminPassword');

  if (adminPassword === process.env.ADMIN_PASSWORD) {
    ws.isAdmin = true;
    adminClients.add(ws);
    logger.info('An admin client connected!');
    ws.send(JSON.stringify({ type: 'activity', data: 'Successfully connected to admin channel.' }));

    ws.on('close', () => {
      adminClients.delete(ws);
      logger.info('An admin client disconnected.');
    });
    // Admin clients just listen, they don't send messages like users.
    // We can add specific admin message handlers here if needed in the future.
    return; 
  }

  logger.info('A new client connected!');

  ws.on('message', (message) => {
    const parsedMessage = JSON.parse(message.toString());
    logger.info('Received message:', parsedMessage);

    switch (parsedMessage.type) {
      case 'user_join': {
        const { userId, username, userColor } = parsedMessage;
        ws.userId = userId;
        onlineUsers.set(userId, { userId, username, userColor });
        if (!allUsers.has(userId)) {
          allUsers.set(userId, { userId, username, userColor });
        }
        logger.info(`User '${username}' joined.`);
        broadcastToAdmins('users', Array.from(allUsers.values()));
        broadcastToAdmins('activity', `User '${username}' connected.`);
        broadcastOnlineUsers();
        ws.send(JSON.stringify({ type: 'history', data: messageHistory }));
        break;
      }
      case 'react': {
        const { messageId, userId, emoji } = parsedMessage;
        const username = allUsers.get(userId)?.username || 'Unknown';
        const message = messageHistory.find(m => m.id === messageId);

        if (message) {
          if (!message.reactions) {
            message.reactions = {};
          }

          let previousEmoji = null;
          // Find and remove any previous reaction from this user
          for (const e in message.reactions) {
            const userIndex = message.reactions[e].findIndex(u => u.userId === userId);
            if (userIndex > -1) {
              previousEmoji = e;
              message.reactions[e].splice(userIndex, 1);
              if (message.reactions[e].length === 0) {
                delete message.reactions[e];
              }
              break; // A user can only have one reaction, so we can stop.
            }
          }

          // If the new reaction is not a toggle-off of the same emoji, add it.
          if (previousEmoji !== emoji) {
            if (!message.reactions[emoji]) {
              message.reactions[emoji] = [];
            }
            message.reactions[emoji].push({ userId, username });
          }

          // Broadcast the update
          wss.clients.forEach(client => {
            if (client.readyState === WebSocket.OPEN) {
              client.send(JSON.stringify({ type: 'update', data: { id: messageId, reactions: message.reactions } }));
            }
          });
        }
        break;
      }      case 'edit': {
        const { messageId, newText } = parsedMessage;
        const msg = messageHistory.find(m => m.id === messageId);
        if (!msg) return;

        const username = allUsers.get(ws.userId)?.username || 'Unknown';
        messageEventsLog.push({
          type: 'edit',
          messageId,
          oldText: msg.text,
          newText,
          userId: ws.userId,
          username,
          timestamp: new Date().toISOString(),
        });
        broadcastToAdmins('history', messageEventsLog);

        msg.text = newText;
        msg.edited = true;
        const updateMsg = { type: 'update', data: msg };
        wss.clients.forEach(c => { if (c.readyState === WebSocket.OPEN) c.send(JSON.stringify(updateMsg)); });
        broadcastToAdmins('activity', `Message (ID: ${messageId}) edited by '${username}'. New text: "${newText}"`);
        break;
      }
      case 'start_typing': {
        if (ws.userId) typingUsers.set(ws.userId, true);
        broadcastOnlineUsers();
        break;
      }
      case 'stop_typing': {
        if (ws.userId) typingUsers.delete(ws.userId);
        broadcastOnlineUsers();
        break;
      }
      case 'delete_for_everyone': {
        const { messageId } = parsedMessage;
        const msgIndex = messageHistory.findIndex(m => m.id === messageId);
        if (msgIndex === -1) return;

        const originalMessage = messageHistory[msgIndex];
        const username = allUsers.get(ws.userId)?.username || 'Unknown';
        messageEventsLog.push({
          type: 'delete_everyone',
          messageId,
          deletedContent: originalMessage,
          userId: ws.userId,
          username,
          timestamp: new Date().toISOString(),
        });
        broadcastToAdmins('history', messageEventsLog);

        messageHistory[msgIndex] = {
            id: originalMessage.id,
            userId: originalMessage.userId,
            username: originalMessage.username,
            userColor: originalMessage.userColor,
            sender: originalMessage.sender,
            timestamp: originalMessage.timestamp,
            isDeleted: true,
            text: undefined,
            type: 'text',
            url: undefined,
            originalName: undefined,
            reactions: undefined,
            edited: false,
            replyingTo: undefined,
        };

        const updateMsg = { type: 'update', data: messageHistory[msgIndex] };
        wss.clients.forEach(c => {
            if (c.readyState === WebSocket.OPEN) {
                c.send(JSON.stringify(updateMsg));
            }
        });
        broadcastToAdmins('activity', `Message (ID: ${messageId}) deleted by '${username}'.`);
        break;
      }
      default: { // New text/media message
        if (ws.userId && typingUsers.has(ws.userId)) {
          typingUsers.delete(ws.userId);
          broadcastOnlineUsers();
        }
        const username = allUsers.get(ws.userId)?.username || 'Unknown';
        const newMsg = { ...parsedMessage, sender: ws.userId };
        messageEventsLog.push({
          type: 'create',
          message: newMsg,
          userId: ws.userId,
          username: username,
          timestamp: new Date().toISOString(),
        });
        broadcastToAdmins('history', messageEventsLog);
        broadcastToAdmins('activity', `New message from '${username}': "${newMsg.text || '[Media]'}"`);
        messageHistory.push(newMsg);
        if (messageHistory.length > MAX_HISTORY) messageHistory.shift();
        wss.clients.forEach(client => {
          if (client !== ws && client.readyState === WebSocket.OPEN) {
            client.send(JSON.stringify(newMsg));
          }
        });
        break;
      }
    }
  });

  ws.on('close', () => {
    if (ws.userId && onlineUsers.has(ws.userId)) {
      const username = allUsers.get(ws.userId)?.username || 'Unknown';
      logger.info(`User '${username}' disconnected.`);
      broadcastToAdmins('users', Array.from(allUsers.values()));
      broadcastToAdmins('activity', `User '${username}' disconnected.`);
      onlineUsers.delete(ws.userId);
      typingUsers.delete(ws.userId);
      broadcastOnlineUsers();
    }
  });
  ws.on('error', (error) => logger.error('WebSocket Error:', { message: error.message }));
});

// --- Start Server ---
server.listen(PORT, () => {
  logger.info(`Server is listening on port ${PORT}`);
});

// Watch for changes in the log file and broadcast to admins
const logFilePath = path.join(__dirname, 'pulse-activity.log');
fs.watch(logFilePath, (eventType, filename) => {
  if (eventType === 'change') {
    fs.readFile(logFilePath, 'utf8', (err, data) => {
      if (err) {
        logger.error('Failed to read log file on change:', err);
        return;
      }
      const lines = data.split('\n').slice(-200).join('\n');
      const payload = { type: 'server_logs', data: lines };
      adminClients.forEach(client => {
        if (client.readyState === WebSocket.OPEN) {
          client.send(JSON.stringify(payload));
        }
      });
    });
  }
});

